function showElementsHeader(){document.querySelector("header").classList.remove("header--is-hidden")}function hiddenElementsHeader(){document.querySelector("header").classList.add("header--is-hidden")}document.addEventListener("DOMContentLoaded",function(){let e=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){(window.scrollY<e?showElementsHeader:hiddenElementsHeader)()})});